var path = require('path');

var coreUiEntryPoint;

try {
    coreUiEntryPoint = path.dirname(require.resolve('mns-core-ui'));
} catch (e) {
    coreUiEntryPoint = process.cwd();
}

var sassDir = path.join(coreUiEntryPoint, 'src');
var assetImageDir = path.join(coreUiEntryPoint, 'dist', 'images');
var assetFontDir = path.join(coreUiEntryPoint, 'dist', 'fonts');

module.exports = {
    sassPaths           : [sassDir],
    assetImagePaths     : assetImageDir,
    assetFontPaths      : assetFontDir
};
