export default class Cookie {

    constructor(documentReference = window.document) {
        this.document = documentReference;
    }

    value(key) {
        let result = null;

        this.document.cookie.split('; ').forEach((cookie) => {
            let [name, value] = cookie.split('=');

            if (name === key) {
                result = decodeURIComponent(value);
                return;
            }
        });

        return result;
    }
}
