import events from '../js/events';

describe('base : events', () => {

    describe('normaliseElements', () => {
        it('should return an array', () => {
            expect(events.normaliseElements([{element:''}])).to.deep.equal([{element:''}]);
            expect(events.normaliseElements({element : ''})).to.deep.equal([{element : ''}]);
            expect(events.normaliseElements(null)).to.deep.equal([]); //getElementById returns null if no matches
            expect(events.normaliseElements([])).to.deep.equal([]); //querySelectorAll returns [] if no matches
        });
    });

    describe('bind', () => {
        it('should throw error if member of array is not a DOM object', () => {
            expect(function() {
                events.bind([{}], {});
            }).to.throw('element is not a DOM object');
        });
    });
});