import FormDesktopPage from './pageObject/form.desktop.page';

describe('Components Form', () => {

    let formDesktopPage = new FormDesktopPage();

    before(()=> {
        formDesktopPage.open();
    });

    describe('Desktop', () => {

        it('Form', () => {

            let result = browser.webdrivercss('components__form--desktop', [
                {
                    name: 'form-panel',
                    elem: '.form-panel',
                    screenWidth: [breakpoint.xlarge.width, breakpoint.large.width]
                }
            ]);

            isWithinMisMatchTolerance(result);
        });

    });
});
