import FormMobilePage from './pageObject/form.mobile.page';

describe('Components Form', () => {

    let formMobilePage = new FormMobilePage();

    before(()=> {
        formMobilePage.open();
    });

    describe('Mobile', () => {
        it('Form', () => {

            let result = browser.webdrivercss('components__form--mobile', [
                {
                    name: 'form-panel',
                    elem: '.form-panel',
                    screenWidth: [breakpoint.xsmall.width, breakpoint.small.width]
                }
            ]);

            isWithinMisMatchTolerance(result);
        });
    });
});