import ColorsDesktopPage from './pageObject/colors.desktop.page';

describe('Global Colors', () => {

    let colorsDesktopPage = new ColorsDesktopPage();

    before(()=> {
        colorsDesktopPage.open();
    });

    describe('Desktop', () => {

        it('Swatches', () => {

            var result = browser.webdrivercss('globals__colors--desktop', [
                {
                    name: 'color-panel',
                    elem: '.color-panel',
                    screenWidth: [breakpoint.xlarge.width, breakpoint.large.width]
                }
            ]);

            isWithinMisMatchTolerance(result);
        });

    });
});
