import ColorsMobilePage from './pageObject/colors.mobile.page';

describe('Global Colors', () => {

    let colorsMobilePage = new ColorsMobilePage();

    before(()=> {
        colorsMobilePage.open();
    });

    describe('Mobile', () => {
        it('Swatches', () => {

            var result = browser.webdrivercss('globals__colors--mobile', [
                {
                    name: 'color-panel',
                    elem: '.color-panel',
                    screenWidth: [breakpoint.xsmall.width, breakpoint.small.width]
                }
            ]);

            isWithinMisMatchTolerance(result);
        });
    });
});