import IconsDesktopPage from './pageObject/icons.desktop.page';

describe('Global Icons', () => {

    let iconsDesktopPage = new IconsDesktopPage();

    before(()=> {
        iconsDesktopPage.open();
    });

    describe('Desktop', () => {

        it('Icons', () => {

            let result = browser.webdrivercss('globals__icons--desktop', [
                {
                    name: 'icon-panel',
                    elem: '.icon-panel',
                    screenWidth: [breakpoint.xlarge.width, breakpoint.large.width]
                }
            ]);

            isWithinMisMatchTolerance(result);
        });

    });
});
