import IconsMobilePage from './pageObject/icons.mobile.page';

describe('Global Icons', () => {

    let iconsMobilePage = new IconsMobilePage();

    before(()=> {
        iconsMobilePage.open();
    });

    describe('Mobile', () => {
        it('Icons', () => {

            let result = browser.webdrivercss('globals__icons--mobile', [
                {
                    name: 'icon-panel',
                    elem: '.icon-panel',
                    screenWidth: [breakpoint.xsmall.width, breakpoint.small.width]
                }
            ]);

            isWithinMisMatchTolerance(result);
        });
    });
});