import TypographyPage from './pageObject/typography.page';

describe('Components : Typography', () => {

    let typographyDesktopPage = new TypographyPage();

    before(()=> {
        typographyDesktopPage.open();
    });

    describe('Desktop', () => {

        it('Typography example', () => {

            var result = browser.webdrivercss('template__navigation--desktop', [
                {
                    name: 'typography-example',
                    elem: 'body',
                    screenWidth: [breakpoint.xlarge.width, breakpoint.large.width]
                }
            ]);

            isWithinMisMatchTolerance(result);
        });
    });
});
