import TypographyPage from './pageObject/typography.page';

describe('Components : Typography', () => {

    let typographyMobilePage = new TypographyPage();

    before(()=> {
        typographyMobilePage.open();
    });

    describe('Mobile', () => {

        it('Typography example', () => {

            var result = browser.webdrivercss('template__navigation--mobile', [
                {
                    name: 'typography-example',
                    elem: 'body',
                    screenWidth: [breakpoint.xsmall.width, breakpoint.small.width]
                }
            ]);

            isWithinMisMatchTolerance(result);
        });
    });
});
