import NavigationMobilePage from './pageObject/navigation.mobile.page';

describe('Global Navigation', () => {

    let navigationMobilePage = new NavigationMobilePage();

    before(()=> {
        navigationMobilePage.open();
    });

    describe('Mobile', () => {
        it('Level 1', () => {

            navigationMobilePage.openHamburgerMenu();

            var result = browser.webdrivercss('template__navigation--mobile', [
                {
                    name: 'level1',
                    elem: '.nav',
                    screenWidth: [breakpoint.xsmall.width, breakpoint.small.width]
                }
            ]);

            isWithinMisMatchTolerance(result);
        });
    });
});