import Page from '../../../../../test/pageObject/page';

const DEFAULT_PATH = 'examples/templates/navigation/body';

export default class NavigationDesktopPage extends Page {

    constructor(path = DEFAULT_PATH) {
        super(path);
    }

    open() {
        super.open();
        this.desktopViewport();
    }

    hoverMenuItem(index) {
        let el = browser.element(`.nav__level1-panel > li:nth-child(${index + 1}) > a`);
        el.moveToObject(10, 10);
        return el;
    }

    get level1MenuItems() {
        return browser.elements('.nav__level1-panel > li > a');
    }

}
