export default class Page {

    constructor(path) {
        this.path = path;
    }

    open() {
        browser.url('/' + this.path);
    }

    mobileViewport() {
        browser.setViewportSize({width: breakpoint.small.width, height: 736}, true);
    }

    desktopViewport() {
        browser.setViewportSize({width: breakpoint.xlarge.width, height: 768}, true);
    }
}